import React, {useContext, useEffect, useState} from "react";
import { useRouter } from "next/router";
import {Button} from 'react-bootstrap';
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { connect } from 'react-redux';
import { DispatchContext } from "../contexts/FormContext";
import ProgressBar from "react-bootstrap/ProgressBar";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import { v4 as uuid } from 'uuid';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';

function PersonalInformationForm(props) {
    const router = useRouter();
    const dispatch = useContext(DispatchContext);
    const [nameFirst, setNameFirst] = useState('');
    const [nameLast, setNameLast] = useState('');
    const [nameEmail, setNameEmail] = useState('');
    const [namePhone, setNamePhone] = useState('');

    dispatch({ type: "ID", id: uuid() });

    useEffect(() => {
            dispatch({ type: "NAME_FIRST", nameFirst: nameFirst });
    }, [nameFirst]);

    useEffect(() => {
            dispatch({ type: "NAME_LAST", nameLast: nameLast });
    }, [nameLast]);

    useEffect(() => {
            dispatch({type: "NAME_EMAIL", nameEmail: nameEmail});
    }, [nameEmail]);

    useEffect(() => {
        dispatch({type: "NAME_PHONE", namePhone: namePhone});
    }, [namePhone]);

    const useStyles = makeStyles(theme => ({
        container: {
          display: 'flex',
          flexWrap: 'wrap',
        },
        textField: {
          marginLeft: theme.spacing(1),
          marginRight: theme.spacing(1),
        },
        dense: {
          marginTop: theme.spacing(2),
        },
        menu: {
          width: 200,
        },
          button: {
            marginTop: theme.spacing(5),
            textAlign:'center'
          },
          paper: {
            padding: theme.spacing(2),
            textAlign: 'center',
            color: theme.palette.text.secondary,
          },
      }));
    const classes = useStyles();
    const handleSubmit = (e) => {
        e.preventDefault();
        router.push('/spouseForm');
    }
    // progress bar
    const now = 0;
    const progressInstance = <ProgressBar style={{height: '0.5rem'}} now={now} />;

    return (
        <React.Fragment>
            <CssBaseline />
            <Grid item xs={12} style={{margin:'10px auto'}}>
                            {progressInstance}
                        </Grid>
            <Container maxWidth="md">
                <div className={classes.root}>
                    <Grid container spacing={3}>
                        <Grid item xs={12}>
                            <h3 className="header">Please enter some information in case we get disconnected</h3>
                        </Grid>
                        <Grid item xs={12}>
                            <form className={classes.container} autoComplete="off" onSubmit={(e)=>{handleSubmit(e)}}>
                            <Grid item xs={12} sm={6} className={classes.paper}>
                                <TextField
                                    required
                                    fullWidth
                                    id="first_name"
                                    label="First Name"
                                    className={classes.textField}
                                    value={nameFirst}
                                    onChange={(e) => {
                                        setNameFirst(e.target.value)
                                    }}
                                />
                            </Grid>
                                <br />
                                <Grid item xs={12} sm={6} className={classes.paper}>
                                <TextField
                                    required
                                    fullWidth
                                    id="last_name"
                                    label="Last Name"
                                    className={classes.textField}
                                    value={nameLast}
                                    onChange={(e) => {
                                        setNameLast(e.target.value)
                                    }}
                                />

                                </Grid>
                                <br />
                                <Grid item xs={12} sm={6} className={classes.paper}>
                                <TextField
                                    required
                                    fullWidth
                                    id="email"
                                    label="Email"
                                    type="email"
                                    className={classes.textField}
                                    value={nameEmail}
                                    onChange={(e) => {
                                        setNameEmail(e.target.value)
                                    }}
                                />
                                </Grid>
                                <br />
                                <Grid item xs={12} sm={6} className={classes.paper}>
                                <TextField
                                    required
                                    fullWidth
                                    id="phone"
                                    label="Phone"
                                    type="number"
                                    className={classes.textField}
                                    value={namePhone}
                                    onChange={(e) => {
                                        setNamePhone(e.target.value)
                                    }}
                                /></Grid>
                                <br />
                                <br />
                                <Grid container justify="center" alignItems="center" className={classes.button}>
                                    <Grid item xs={12} sm={4} >
                                        <Button as="input" type="submit" value="Next" style={{ width: "100%" }}/>
                                    </Grid>
                                </Grid>
                            </form>
                        </Grid>
                    </Grid>
                </div>     
            </Container>
        </React.Fragment>
    )
}

const mapStateToProps = function(state) {
    return {
      profile: state.user.profile,
      loggedIn: state.auth.loggedIn
    }
  }
  
  export default connect(mapStateToProps)(PersonalInformationForm);
