import React, { useContext, useState } from "react";
import { useRouter } from "next/router";
import { DispatchContext } from "../contexts/FormContext";
import DatePicker from "react-datepicker";
import {QuestionCircle} from "react-bootstrap-icons";
import {Modal, ProgressBar, Button, Form, Col, Row} from "react-bootstrap";
import {Grid, Container, TextField, CssBaseline} from '@material-ui/core';


export default function DobForm() {
    const router = useRouter();
    const dispatch = useContext(DispatchContext);
    const [showDob, setShowDob] = useState(false);
    const handleCloseDob = () => setShowDob(false);

    const now = 35;
    const [startDate, setStartDate] = useState(new Date());
    function handleSubmit(e){
        e.preventDefault();
        console.log('form submit dob', startDate)
        dispatch({ type: "DOB", dob: startDate });
        router.push('/gender');
    }
    return (
        <React.Fragment>
			<CssBaseline />
			<Grid item xs={12} style={{margin:'10px auto'}}>
			<ProgressBar style={{height: '0.5rem'}} animated striped variant="info" label={`${now}% Done`} now={now} />
			</Grid>
			<Container maxWidth="sm" style={{marginTop:'40px'}}>
				<Grid container justify="center" xs={12} sm={12} spacing={2}>
					<h3 className='header'>What is your date of birth?</h3>
				</Grid>
                <Grid container xs={12} sm={12} justify="center" spacing={2}>
                    <Grid item xs={12} sm={6}>
                        <DatePicker
                            selected={startDate}
                            onChange={date => setStartDate(date)}
                            className="dobCSS"
                        />
                    </Grid> 
                </Grid>
                <Grid container justify="center" xs={12} sm={12} spacing={2} className="mt-4">
                    <Grid item xs={12} sm={6} >
                        <Button
                            style={{ width: "100%" }}
                            onClick={(e)=>{handleSubmit(e)}}
                            variant="outline-info"
                            size="lg"
                        >
                            Next
                        </Button>
                    </Grid>
                </Grid>
                <Grid container justify="center" xs={12} sm={12} spacing={2} className="mt-4">
					<p className="header font-sm-weight-bold">Why do we ask about your date of birth?
						<QuestionCircle
							size={20}
							onClick={() => {setShowDob(true)}}
							style={{marginLeft:'30px'}}
						/>
					</p>
				</Grid>
            {/*ask about children*/}
            <Modal show={showDob} onHide={handleCloseDob}>
                <Modal.Body>Life insurance companies use a method called “age nearest” to determine the age the policy premiums are based upon. The age nearest method calculates the issue age based on the nearest age to your policy date. For example, if your date of birth is February 20th, 1984 and today’s date is January 20th, 2019, the issue age would be 35, even though your actual age is 34 and you won’t be 35 for another month.</Modal.Body>
                <Modal.Footer>
                    <Button variant="outline-info" onClick={handleCloseDob}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
            </Container>
        </React.Fragment>
    )
}
