import React, {useContext, useEffect, useState} from "react";
import { useRouter } from "next/router";
import Button from '@material-ui/core/Button';
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { DispatchContext } from "../contexts/FormContext";
import ProgressBar from "react-bootstrap/ProgressBar";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import { v4 as uuid } from 'uuid';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';

export default function PersonalInformationForm() {
    const router = useRouter();
    const dispatch = useContext(DispatchContext);
    const [nameFirst, setNameFirst] = useState('');
    const [nameLast, setNameLast] = useState('');
    const [nameEmail, setNameEmail] = useState('');
    const [namePhone, setNamePhone] = useState('');

    dispatch({ type: "ID", id: uuid() });

    useEffect(() => {
            dispatch({ type: "NAME_FIRST", nameFirst: nameFirst });
    }, [nameFirst]);

    useEffect(() => {
            dispatch({ type: "NAME_LAST", nameLast: nameLast });
    }, [nameLast]);

    useEffect(() => {
            dispatch({type: "NAME_EMAIL", nameEmail: nameEmail});
    }, [nameEmail]);

    useEffect(() => {
        dispatch({type: "NAME_PHONE", namePhone: namePhone});
    }, [namePhone]);

    const useStyles = makeStyles(theme => ({
        container: {
          display: 'flex',
          flexWrap: 'wrap',
        },
        textField: {
          marginLeft: theme.spacing(1),
          marginRight: theme.spacing(1),
        },
        dense: {
          marginTop: theme.spacing(2),
        },
        menu: {
          width: 200,
        },
          button: {
            margin: theme.spacing(1),
          },
      }));

    const classes = useStyles();

    // progress bar
    const now = 0;
    const progressInstance = <ProgressBar style={{height: '0.5rem'}} now={now} />;

    return (
        <React.Fragment>
            <CssBaseline />
            <Container maxWidth="md p-2">
                <div className={classes.root}>
                    <Grid container spacing={3}>
                        <Grid item xs={12}>
                            {progressInstance}
                        </Grid>
                        <Grid item xs={12}>
                            <h3 className="header">Please enter some information in case we get disconnected</h3>
                        </Grid>
                        <Grid item xs={12}>
                            <form className={classes.container} Validate autoComplete="off">
                                <TextField
                                    required
                                    fullWidth
                                    id="first_name"
                                    label="First Name"
                                    className={classes.textField}
                                    value={nameFirst}
                                    onChange={(e) => {
                                        setNameFirst(e.target.value)
                                    }}
                                />
                                <TextField
                                    required
                                    fullWidth
                                    id="last_name"
                                    label="Last Name"
                                    className={classes.textField}
                                    value={nameLast}
                                    onChange={(e) => {
                                        setNameLast(e.target.value)
                                    }}
                                />
                                <TextField
                                    required
                                    fullWidth
                                    id="email"
                                    label="Email"
                                    className={classes.textField}
                                    value={nameEmail}
                                    onChange={(e) => {
                                        setNameEmail(e.target.value)
                                    }}
                                />
                                <TextField
                                    required
                                    fullWidth
                                    id="phone"
                                    label="Phone"
                                    className={classes.textField}
                                    value={namePhone}
                                    onChange={(e) => {
                                        setNamePhone(e.target.value)
                                    }}
                                />
                                <Button variant="contained" color="primary" className={classes.button} onClick={()=> {router.push('/spouseForm')}}>
                                    Next
                                </Button>
                                                            {/* <Button
                                    style={{ width: "100%" }}
                                    variant="outline-primary"
                                    size="lg"
                                    onClick={()=> {router.push('/spouseForm')}}
                                >
                                    Next
                                </Button> */}
                            </form>
                        </Grid>
                    </Grid>
                </div>     
            </Container>
        </React.Fragment>
    )
}
